import React, { useState, useEffect } from "react";
import axios from "axios";

function YourTickets() {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    // Get user ID from local storage
    const u_id = localStorage.getItem("u_id");

    // Fetch tickets using Axios
    axios
      .get(`/get_tickets?u_id=${u_id}`)
      .then((response) => {
        setTickets(response.data);
      })
      .catch((error) => {
        console.error("Error fetching tickets:", error);
      });
  }, []); // Empty dependency array ensures useEffect only runs once

  return (
    <div className="your_tickets">
      {tickets.map((ticket) => (
        <div key={ticket.t_id} className="ticket_info">
          <p>Ticket ID: {ticket.t_id}</p>
          <p>Event ID: {ticket.e_id}</p>
          <p>Price: {ticket.price}</p>
          <p>Bid Price: {ticket.bid_price}</p>
          <p>Owner: {ticket.owner}</p>
          <p>Final Owner: {ticket.f_owner}</p>
          <p>Category: {ticket.category}</p>
        </div>
      ))}
    </div>
  );
}

export default YourTickets;
