import React, { useState } from "react";

function Card2({ onNextClick }) {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [verification, setVerification] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");

  const handleNextClick = (event) => {
    event.preventDefault(); // Prevent the form from submitting

    // Validate the form data here if needed

    // Proceed to the next step (Card3) or perform any other action
    onNextClick();
  };

  return (
    <div className="login-container">
      <h2>Tell us more about yourself!</h2>
      <form onSubmit={handleNextClick}>
        <input
          type="text"
          className="input-field"
          placeholder="Name"
          name="username"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          className="input-field"
          placeholder="Phone"
          name="phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <input
          type="email"
          className="input-field"
          placeholder="Email Address"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <div className="verification-container">
          <div className="verification-radio">
            <input
              type="radio"
              id="phone-verification"
              name="verification"
              value="phone"
              checked={verification === "phone"}
              onChange={() => setVerification("phone")}
            />
            <label htmlFor="phone-verification">Phone Verification</label>
          </div>
          <div className="verification-radio">
            <input
              type="radio"
              id="email-verification"
              name="verification"
              value="email"
              checked={verification === "email"}
              onChange={() => setVerification("email")}
            />
            <label htmlFor="email-verification">Email Verification</label>
          </div>
        </div>
        <br></br>
        <label htmlFor="age">Age:</label>
        <input
          type="date"
          id="age"
          name="age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
          required
        />
        <label htmlFor="gender">Gender:</label>
        <select
          name="gender"
          id="gender"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          required
        >
          <option value="">Select Gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="non-binary">Non-Binary</option>
        </select>
        <br></br>

        <button type="submit" className="login-button">
          Next
        </button>
      </form>
      <a href="#" className="signup-link">
        Not interested? Skip
      </a>
    </div>
  );
}

export default Card2;
