import React, { useState, useEffect } from "react";
import axios from "axios";

function View1() {
  const [tickets, setTickets] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);

  useEffect(() => {
    // Fetch tickets based on e_id from backend
    axios
      .get(`http://localhost:5000/api/tickets?e_id=${1}`)
      .then((response) => {
        setTickets(response.data);
      })
      .catch((error) => {
        console.error("Error fetching tickets:", error);
      });
  }, []);

  // Helper function to check if a seat is selectable based on ticket data
  const isSeatSelectable = (seatId) => {
    return !tickets.some((ticket) => ticket.t_id === seatId);
  };

  // Function to handle seat selection
  const handleSeatSelection = (seatId) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(seatId)) {
        return prevSelectedSeats.filter((id) => id !== seatId);
      } else {
        return [...prevSelectedSeats, seatId];
      }
    });
  };

  // Render the seats
  const renderSeats = () => {
    const numSeats = 50; // Adjust as per your layout
    const seats = [];
    for (let i = 1; i <= numSeats; i++) {
      const isSelectable = isSeatSelectable(i);
      const isSelected = selectedSeats.includes(i);
      seats.push(
        <div
          key={i}
          className={`seat ${isSelectable ? "selectable" : ""} ${
            isSelected ? "selected" : ""
          }`}
          onClick={() => isSelectable && handleSeatSelection(i)}
        >
          {i}
        </div>
      );
    }
    return seats;
  };

  return (
    <div>
      <h2>Stage</h2>
      <div className="venue">
        {/* Render seats */}
        {renderSeats()}
      </div>
    </div>
  );
}

export default View1;
