import React, { useState, useEffect } from "react";
import axios from "axios";

function View1() {
  const [tickets, setTickets] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);

  useEffect(() => {
    // Fetch tickets based on e_id from backend
    axios
      .get(`/api/tickets?e_id=${YOUR_EVENT_ID}`)
      .then((response) => {
        setTickets(response.data);
      })
      .catch((error) => {
        console.error("Error fetching tickets:", error);
      });
  }, []);

  const handleSeatSelection = (ticketId) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(ticketId)) {
        return prevSelectedSeats.filter((id) => id !== ticketId);
      } else {
        return [...prevSelectedSeats, ticketId];
      }
    });
  };

  return (
    <div>
      <div className="venue">
        {/* Render selectable seats here */}
        {tickets.map((ticket) => (
          <div
            key={ticket.t_id}
            className={`seat ${
              selectedSeats.includes(ticket.t_id) ? "selected" : ""
            }`}
            onClick={() => handleSeatSelection(ticket.t_id)}
          >
            {ticket.category === "premium" ? "P" : "R"}
            {/* Premium or Regular */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default View1;
